  try {
    const id = request.params.id,
      { name, uprice } = request.body;

    //Si existe
    if (request.file) {
      const updatedProduct = await Product.findByIdAndUpdate(
        id,
        {
          name,
          uprice,
          image: request.file.path,
        },
        { new: true }
      );
      response.status(200).json({
        message: "Producto actualizado correctamente",
        updatedProduct,
      });
    } else {
      const updatedProduct = await Product.findByIdAndUpdate(
        id,
        {
          name,
